import React from 'react';
import { View, Text, StyleSheet, ScrollView, Pressable } from 'react-native';
import { Feather } from '@expo/vector-icons';

interface Lesson {
  id: string;
  title: string;
  duration: number;
  completed: boolean;
}

interface Module {
  id: string;
  title: string;
  lessons: Lesson[];
}

interface LessonListProps {
  modules: Module[];
  currentLessonId: string;
  onLessonSelect: (lessonId: string) => void;
}

export const LessonList = ({ modules, currentLessonId, onLessonSelect }: LessonListProps) => {
  const formatDuration = (minutes: number) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0
      ? `${hours}h ${mins}m`
      : `${mins}m`;
  };

  return (
    <ScrollView style={styles.container}>
      {modules.map((module) => (
        <View key={module.id} style={styles.module}>
          <Text style={styles.moduleTitle}>{module.title}</Text>
          {module.lessons.map((lesson) => (
            <Pressable
              key={lesson.id}
              style={[
                styles.lessonItem,
                currentLessonId === lesson.id && styles.currentLesson,
              ]}
              onPress={() => onLessonSelect(lesson.id)}
            >
              <View style={styles.lessonContent}>
                <View style={styles.lessonInfo}>
                  {lesson.completed ? (
                    <Feather name="check-circle" size={20} color="#059669" />
                  ) : (
                    <View style={styles.progressCircle} />
                  )}
                  <Text
                    style={[
                      styles.lessonTitle,
                      lesson.completed && styles.completedLessonTitle,
                    ]}
                    numberOfLines={2}
                  >
                    {lesson.title}
                  </Text>
                </View>
                <Text style={styles.duration}>
                  {formatDuration(lesson.duration)}
                </Text>
              </View>
            </Pressable>
          ))}
        </View>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
  },
  module: {
    paddingVertical: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
  },
  moduleTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: '#111827',
    paddingHorizontal: 16,
    marginBottom: 12,
  },
  lessonItem: {
    paddingVertical: 12,
    paddingHorizontal: 16,
  },
  currentLesson: {
    backgroundColor: '#F3F4F6',
  },
  lessonContent: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  lessonInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    flex: 1,
    marginRight: 16,
  },
  progressCircle: {
    width: 20,
    height: 20,
    borderRadius: 10,
    borderWidth: 2,
    borderColor: '#D1D5DB',
  },
  lessonTitle: {
    fontSize: 16,
    color: '#374151',
    marginLeft: 12,
    flex: 1,
  },
  completedLessonTitle: {
    color: '#059669',
  },
  duration: {
    fontSize: 14,
    color: '#6B7280',
  },
});
