import React from 'react';
import { View, Text, ScrollView, StyleSheet, Platform } from 'react-native';
import { CourseCard } from '../components/course/CourseCard';

// Mock data for testing
const mockCourses = [
  {
    id: '1',
    title: 'Complete Web Development',
    category: 'Web Development',
    duration: '20h 30m',
    rating: 4.8,
    thumbnail: 'https://picsum.photos/800/600',
    instructor: 'John Doe'
  },
  {
    id: '2',
    title: 'Mobile App Development',
    category: 'Mobile Development',
    duration: '18h 45m',
    rating: 4.9,
    thumbnail: 'https://picsum.photos/800/600?random=2',
    instructor: 'Jane Smith'
  },
  {
    id: '3',
    title: 'Data Science Fundamentals',
    category: 'Data Science',
    duration: '15h 15m',
    rating: 4.7,
    thumbnail: 'https://picsum.photos/800/600?random=3',
    instructor: 'Mike Johnson'
  }
];

export const HomeScreen = ({ navigation }) => {
  return (
    <ScrollView 
      style={styles.container}
      contentContainerStyle={styles.contentContainer}
      showsVerticalScrollIndicator={false}
    >
      <View style={styles.header}>
        <Text style={styles.welcomeText}>Welcome to SKiddy</Text>
        <Text style={styles.subText}>Start your learning journey today</Text>
      </View>

      <View style={styles.coursesSection}>
        <Text style={styles.sectionTitle}>Featured Courses</Text>
        <View style={styles.courseGrid}>
          {mockCourses.map((course) => (
            <View key={course.id} style={styles.cardWrapper}>
              <CourseCard
                title={course.title}
                category={course.category}
                duration={course.duration}
                rating={course.rating}
                thumbnail={course.thumbnail}
                instructor={course.instructor}
                onPress={() => navigation.navigate('CoursePlayer', { courseId: course.id })}
              />
            </View>
          ))}
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  contentContainer: {
    padding: 24,
  },
  header: {
    marginBottom: 32,
  },
  welcomeText: {
    fontSize: 32,
    fontWeight: '700',
    color: '#111827',
    marginBottom: 8,
  },
  subText: {
    fontSize: 18,
    color: '#6B7280',
  },
  coursesSection: {
    marginBottom: 32,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 24,
  },
  courseGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: 24,
    justifyContent: Platform.OS === 'web' ? 'flex-start' : 'center',
  },
  cardWrapper: {
    flex: Platform.OS === 'web' ? 1 : undefined,
    minWidth: Platform.OS === 'web' ? 300 : undefined,
    maxWidth: Platform.OS === 'web' ? 400 : 340,
  }
});
