import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

export const Header = () => {
  return (
    <View style={styles.header}>
      <View style={styles.container}>
        {/* Logo */}
        <View style={styles.logoContainer}>
          <Text style={styles.logo}>SKiddy</Text>
        </View>

        {/* Course Path Breadcrumb */}
        <View style={styles.breadcrumb}>
          <Text style={styles.breadcrumbText}>Dashboard</Text>
          <Text style={styles.breadcrumbSeparator}>/</Text>
          <Text style={styles.breadcrumbText}>Current Course</Text>
        </View>

        {/* User Actions */}
        <View style={styles.actions}>
          <Text style={styles.actionText}>Notifications</Text>
          <Text style={[styles.actionText, styles.actionSeparator]}>Profile</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    width: '100%',
    backgroundColor: 'white',
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
  },
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 16,
    height: 64,
  },
  logoContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#7C3AED',
  },
  breadcrumb: {
    flexDirection: 'row',
    alignItems: 'center',
    display: 'none',
    '@media (min-width: 768px)': {
      display: 'flex',
    },
  },
  breadcrumbText: {
    color: '#1F2937',
  },
  breadcrumbSeparator: {
    color: '#9CA3AF',
    marginHorizontal: 8,
  },
  actions: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 16,
  },
  actionText: {
    color: '#1F2937',
  },
  actionSeparator: {
    marginLeft: 16,
  },
});
