import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { View, StyleSheet, Platform, useWindowDimensions } from 'react-native';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { GestureHandlerRootView } from 'react-native-gesture-handler';
import { MyCoursesScreen } from './src/screens/MyCoursesScreen';
import { CourseDetailScreen } from './src/screens/CourseDetailScreen';
import { SettingsScreen } from './src/screens/SettingsScreen';
import { HelpScreen } from './src/screens/HelpScreen';
import { CoursePlayerScreen } from './src/screens/CoursePlayerScreen';
import { HomeScreen } from './src/screens/HomeScreen';
import { LoginScreen } from './src/screens/LoginScreen';
import { Sidebar } from './src/components/layout/Sidebar';
import { Header } from './src/components/layout/Header';
import { AuthProvider, useAuth } from './src/context/AuthContext';

const Stack = createNativeStackNavigator();

const AppContent = () => {
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const [currentRoute, setCurrentRoute] = useState('/home');
  const { isAuthenticated } = useAuth();

  const handleNavigation = (route: string) => {
    const screenName = route.replace('/', '').split('-').map(
      word => word.charAt(0).toUpperCase() + word.slice(1)
    ).join('');
    // Use the navigation prop passed from NavigationContainer instead
    navigation.navigate(screenName);
  };

  if (!isAuthenticated) {
    return (
      <Stack.Navigator
        screenOptions={{
          headerShown: false,
          animation: Platform.OS === 'web' ? 'none' : 'default',
        }}
      >
        <Stack.Screen name="Login" component={LoginScreen} />
      </Stack.Navigator>
    );
  }

  return (
    <View style={styles.appContainer}>
      <Header />
      <View style={styles.mainLayout}>
        {!isMobile && (
          <Sidebar 
            navigation={{ navigate: handleNavigation }}
            currentRoute={currentRoute}
          />
        )}
        <View style={styles.content}>
          <Stack.Navigator
            screenOptions={{
              headerShown: false,
              animation: Platform.OS === 'web' ? 'none' : 'default',
            }}
          >
            <Stack.Screen name="Home" component={HomeScreen} />
            <Stack.Screen name="MyCourses" component={MyCoursesScreen} />
            <Stack.Screen name="CourseDetail" component={CourseDetailScreen} />
            <Stack.Screen name="CoursePlayer" component={CoursePlayerScreen} options={{ headerShown: false }} />
            <Stack.Screen name="Settings" component={SettingsScreen} />
            <Stack.Screen name="Help" component={HelpScreen} />
          </Stack.Navigator>
        </View>
      </View>
    </View>
  );
};

export default function App() {
  return (
    <GestureHandlerRootView style={styles.container}>
      <SafeAreaProvider>
        <AuthProvider>
          <NavigationContainer>
            <AppContent />
          </NavigationContainer>
        </AuthProvider>
      </SafeAreaProvider>
    </GestureHandlerRootView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  appContainer: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  mainLayout: {
    flex: 1,
    flexDirection: 'row',
  },
  content: {
    flex: 1,
  },
  screenContent: {
    backgroundColor: '#F9FAFB',
  },
});
