import React, { useState } from 'react';
import { View, Text, ScrollView, TextInput, StyleSheet, FlatList, useWindowDimensions, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { CourseCard } from '../components/course/CourseCard';

// Mock data for testing
const mockCourses = [
  {
    id: '1',
    title: 'Introduction to React Native',
    category: 'Mobile Development',
    duration: '6h 30m',
    rating: 4.8,
    thumbnail: 'https://picsum.photos/800/600',
  },
  {
    id: '2',
    title: 'Advanced JavaScript Concepts',
    category: 'Web Development',
    duration: '8h 45m',
    rating: 4.9,
    thumbnail: 'https://picsum.photos/800/600?random=2',
  },
  // Add more mock courses as needed
];

export const HomeScreen = ({ navigation }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const { width } = useWindowDimensions();

  // Calculate number of columns based on screen width
  const getNumColumns = () => {
    if (width < 768) return 1; // Mobile
    if (width < 1024) return 2; // Tablet
    return 3; // Desktop
  };

  const renderCourseCard = ({ item }) => (
    <View style={styles.cardWrapper}>
      <CourseCard
        title={item.title}
        category={item.category}
        duration={item.duration}
        rating={item.rating}
        thumbnail={item.thumbnail}
        onPress={() => navigation.navigate('CourseDetails', { courseId: item.id })}
      />
    </View>
  );

  return (
    <ScrollView 
      style={styles.container}
      contentContainerStyle={styles.contentContainer}
      showsVerticalScrollIndicator={false}
    >
      <View style={styles.searchContainer}>
        <View style={styles.searchWrapper}>
          <Feather name="search" size={20} color="#6B7280" style={styles.searchIcon} />
          <TextInput
            style={styles.searchInput}
            placeholder="Search courses..."
            placeholderTextColor="#6B7280"
            value={searchQuery}
            onChangeText={setSearchQuery}
          />
        </View>
      </View>

      <View style={styles.featuredSection}>
        <Text style={styles.sectionTitle}>Featured Courses</Text>
        <FlatList
          data={mockCourses.slice(0, 3)}
          renderItem={renderCourseCard}
          keyExtractor={(item) => item.id}
          horizontal
          showsHorizontalScrollIndicator={false}
          contentContainerStyle={styles.featuredList}
          ItemSeparatorComponent={() => <View style={styles.cardSeparator} />}
          snapToInterval={width < 768 ? width - 48 : 340 + 24}
          decelerationRate="fast"
          snapToAlignment="start"
        />
      </View>

      <View style={styles.allCoursesSection}>
        <Text style={styles.sectionTitle}>All Courses</Text>
        <FlatList
          data={mockCourses}
          renderItem={renderCourseCard}
          keyExtractor={(item) => item.id}
          numColumns={getNumColumns()}
          columnWrapperStyle={width >= 768 ? styles.courseGrid : undefined}
          showsVerticalScrollIndicator={false}
          scrollEnabled={false}
          ItemSeparatorComponent={() => <View style={styles.rowSeparator} />}
        />
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  contentContainer: {
    padding: 24,
  },
  searchContainer: {
    marginBottom: 32,
    alignItems: 'center',
  },
  searchWrapper: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 12,
    paddingHorizontal: 16,
    height: 48,
    width: '100%',
    maxWidth: 640,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  searchIcon: {
    marginRight: 12,
  },
  searchInput: {
    flex: 1,
    height: '100%',
    fontSize: 16,
    color: '#1F2937',
  },
  featuredSection: {
    marginBottom: 40,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: '700',
    color: '#1F2937',
    marginBottom: 20,
  },
  featuredList: {
    paddingHorizontal: 4,
  },
  allCoursesSection: {
    flex: 1,
  },
  cardWrapper: {
    flex: 1,
    minWidth: 340,
    maxWidth: Platform.select({ web: 340, default: undefined }),
  },
  cardSeparator: {
    width: 24,
  },
  rowSeparator: {
    height: 24,
  },
  courseGrid: {
    justifyContent: 'flex-start',
    gap: 24,
  },
});
