import React from 'react';
import { View, Text, Image, StyleSheet, Pressable, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';

interface CourseCardProps {
  title: string;
  category: string;
  duration: string;
  rating: number;
  thumbnail: string;
  instructor: string;
  progress?: number;
  status?: 'in_progress' | 'completed' | 'upcoming';
  lastAccessedLesson?: string;
  onPress: () => void;
  onResume?: () => void;
  onUnenroll?: () => void;
  onSave?: () => void;
  onRemoveFromSaved?: () => void;
  isSaved?: boolean;
}

export const CourseCard = ({ 
  title, 
  category, 
  duration, 
  rating, 
  thumbnail, 
  instructor,
  progress,
  status,
  lastAccessedLesson,
  onPress,
  onResume,
  onUnenroll,
  onSave,
  onRemoveFromSaved,
  isSaved
}: CourseCardProps) => {
  const getStatusColor = () => {
    switch(status) {
      case 'in_progress': return '#7C3AED';
      case 'completed': return '#059669';
      case 'upcoming': return '#D97706';
      default: return '#6B7280';
    }
  };

  const getStatusLabel = () => {
    switch(status) {
      case 'in_progress': return 'In Progress';
      case 'completed': return 'Completed';
      case 'upcoming': return 'Upcoming';
      default: return '';
    }
  };

  return (
    <Pressable style={styles.card} onPress={onPress}>
      <Image
        source={{ uri: thumbnail }}
        style={styles.thumbnail}
        resizeMode="cover"
      />

      <View style={styles.content}>
        <View style={styles.header}>
          <Text style={styles.title} numberOfLines={2}>{title}</Text>
          <View style={styles.categoryContainer}>
            <Text style={styles.category}>{category}</Text>
          </View>
        </View>

        <View style={styles.instructorRow}>
          <Feather name="user" size={14} color="#6B7280" />
          <Text style={styles.instructor}>{instructor}</Text>
        </View>

        <View style={styles.statsRow}>
          <View style={styles.stat}>
            <Feather name="clock" size={14} color="#6B7280" />
            <Text style={styles.statText}>{duration}</Text>
          </View>
          <View style={styles.stat}>
            <Feather name="star" size={14} color="#6B7280" />
            <Text style={styles.statText}>{rating}</Text>
          </View>
        </View>

        {progress !== undefined && (
          <View style={styles.progressContainer}>
            <View style={styles.progressBar}>
              <View style={[styles.progressFill, { width: `${progress}%` }]} />
            </View>
            <Text style={styles.progressText}>{progress}% Complete</Text>
          </View>
        )}

        {status && (
          <View style={styles.statusContainer}>
            <View style={[styles.statusDot, { backgroundColor: getStatusColor() }]} />
            <Text style={[styles.statusText, { color: getStatusColor() }]}>{getStatusLabel()}</Text>
          </View>
        )}

        <View style={styles.actions}>
          {onResume && (
            <Pressable style={styles.actionButton} onPress={onResume}>
              <Feather name="play-circle" size={20} color="#7C3AED" />
              <Text style={styles.actionText}>Resume</Text>
            </Pressable>
          )}

          {(onSave || onRemoveFromSaved) && (
            <Pressable 
              style={styles.actionButton} 
              onPress={isSaved ? onRemoveFromSaved : onSave}
            >
              <Feather 
                name={isSaved ? "bookmark" : "bookmark-plus"} 
                size={20} 
                color={isSaved ? "#7C3AED" : "#6B7280"} 
              />
              <Text style={[styles.actionText, isSaved && styles.savedText]}>
                {isSaved ? 'Saved' : 'Save'}
              </Text>
            </Pressable>
          )}

          {onUnenroll && (
            <Pressable style={styles.actionButton} onPress={onUnenroll}>
              <Feather name="x-circle" size={20} color="#EF4444" />
              <Text style={[styles.actionText, styles.unenrollText]}>Unenroll</Text>
            </Pressable>
          )}
        </View>
      </View>
    </Pressable>
  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: 'white',
    borderRadius: 16,
    overflow: 'hidden',
    marginBottom: 20,
    boxShadow: Platform.OS === 'web' ? '0 4px 6px rgba(0, 0, 0, 0.1)' : undefined,
    elevation: Platform.OS === 'android' ? 4 : undefined,
    width: '100%',
    maxWidth: 340,
    transform: [{ scale: 1 }],
  },
  thumbnail: {
    width: '100%',
    height: 180,
  },
  content: {
    padding: 16,
  },
  header: {
    marginBottom: 16,
  },
  title: {
    fontSize: 17,
    fontWeight: '700',
    color: '#1F2937',
    marginBottom: 4,
    lineHeight: 24,
  },
  categoryContainer: {
    marginBottom: 8,
  },
  category: {
    color: '#7C3AED',
    fontSize: 13,
    fontWeight: '600',
    textTransform: 'uppercase',
    letterSpacing: 0.5,
  },
  instructorRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  instructor: {
    marginLeft: 8,
    fontSize: 14,
    color: '#6B7280',
  },
  statsRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 16,
  },
  stat: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  statText: {
    marginLeft: 6,
    fontSize: 14,
    color: '#6B7280',
  },
  progressContainer: {
    marginBottom: 16,
  },
  progressBar: {
    height: 4,
    backgroundColor: '#E5E7EB',
    borderRadius: 2,
    marginBottom: 8,
  },
  progressFill: {
    height: '100%',
    backgroundColor: '#7C3AED',
    borderRadius: 2,
  },
  progressText: {
    fontSize: 12,
    color: '#6B7280',
    fontWeight: '500',
  },
  statusContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  statusDot: {
    width: 6,
    height: 6,
    borderRadius: 3,
    marginRight: 6,
  },
  statusText: {
    fontSize: 12,
    fontWeight: '600',
  },
  actions: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  actionButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#F3F4F6',
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 6,
    marginRight: 8,
  },
  actionText: {
    marginLeft: 6,
    fontSize: 12,
    fontWeight: '600',
    color: '#6B7280',
  },
  savedText: {
    color: '#7C3AED',
  },
  unenrollText: {
    color: '#EF4444',
  },
});
