import React, { useState } from 'react';
import { View, StyleSheet, Platform, Pressable, Text } from 'react-native';
import { Feather } from '@expo/vector-icons';

interface VideoPlayerProps {
  videoId: string;
  accessToken: string;
  onComplete?: () => void;
  onProgress?: (progress: number) => void;
  onTimeUpdate?: (time: number) => void;
  initialProgress?: number;
  isLocked?: boolean;
  onSubscribeClick?: () => void;
}

export const VideoPlayer = ({
  videoId,
  accessToken,
  onComplete,
  onProgress,
  onTimeUpdate,
  initialProgress = 0,
  isLocked = false,
  onSubscribeClick,
}: VideoPlayerProps) => {
  const [isFullscreen, setIsFullscreen] = useState(false);
  const embedUrl = `https://app.tpstreams.com/embed/6u448b/${videoId}/?access_token=${accessToken}`;

  const VideoComponent = () => {
    if (Platform.OS === 'web') {
      return (
        <iframe
          src={embedUrl}
          style={{
            width: '100%',
            height: '100%',
            border: 'none',
            backgroundColor: 'black',
          }}
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        />
      );
    }
    // For native platforms, we'll implement native video player later
    return (
      <View style={{ flex: 1, backgroundColor: 'black', justifyContent: 'center', alignItems: 'center' }}>
        <Text style={{ color: 'white' }}>Video player not available on this platform</Text>
      </View>
    );
  };

  if (isLocked) {
    return (
      <View style={[styles.container, styles.lockedContainer]}>
        <View style={styles.lockOverlay}>
          <Feather name="lock" size={48} color="white" />
          <Text style={styles.lockedText}>This content is locked</Text>
          <Text style={styles.lockedSubtext}>
            Subscribe to unlock all courses and videos
          </Text>
          <Pressable 
            style={styles.subscribeButton}
            onPress={onSubscribeClick}
          >
            <Text style={styles.subscribeButtonText}>Subscribe Now</Text>
          </Pressable>
        </View>
        <View style={styles.blurOverlay}>
          <View style={{
            width: '100%',
            height: '100%',
            backgroundColor: 'black',
            opacity: 0.3,
          }} />
        </View>
      </View>
    );
  }

  return (
    <View style={[styles.container, isFullscreen && styles.fullscreen]}>
      <VideoComponent />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    borderRadius: 12,
    overflow: 'hidden',
    aspectRatio: 16 / 9,
  },
  fullscreen: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 999,
  },
  lockedContainer: {
    position: 'relative',
  },
  lockOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    zIndex: 2,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  blurOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 1,
  },
  lockedText: {
    color: 'white',
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 16,
    textAlign: 'center',
  },
  lockedSubtext: {
    color: 'rgba(255, 255, 255, 0.8)',
    fontSize: 16,
    marginTop: 8,
    textAlign: 'center',
  },
  subscribeButton: {
    backgroundColor: '#7C3AED',
    paddingHorizontal: 24,
    paddingVertical: 12,
    borderRadius: 8,
    marginTop: 24,
  },
  subscribeButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: '600',
  },
});
