import React, { useState } from 'react';
import { View, Text, Pressable, StyleSheet, useWindowDimensions, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';
import Animated, { 
  useAnimatedStyle, 
  withSpring,
  useSharedValue
} from 'react-native-reanimated';

interface NavItemProps {
  iconName: keyof typeof Feather.glyphMap;
  label: string;
  isCollapsed: boolean;
  isActive?: boolean;
  onPress: () => void;
}

const NavItem = ({ iconName, label, isCollapsed, isActive, onPress }: NavItemProps) => (
  <Pressable
    style={[styles.navItem, isActive && styles.navItemActive]}
    onPress={onPress}
  >
    <View style={[styles.navIcon, isActive && styles.navIconActive]}>
      <Feather name={iconName} size={18} color={isActive ? '#7C3AED' : '#4B5563'} />
    </View>
    {!isCollapsed && (
      <Text style={[styles.navLabel, isActive && styles.navLabelActive]}>{label}</Text>
    )}
  </Pressable>
);

export const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [activeItem, setActiveItem] = useState('courses');
  const { width } = useWindowDimensions();
  const isMobile = width < 768;

  const sidebarStyle = useAnimatedStyle(() => {
    return {
      width: withSpring(isCollapsed ? 64 : 240, {
        damping: 15,
        stiffness: 100,
      }),
    };
  }, [isCollapsed]);

  return (
    <Animated.View style={[styles.sidebar, sidebarStyle]}>
      {/* Toggle Button */}
      <Pressable style={styles.toggleButton} onPress={() => setIsCollapsed(!isCollapsed)}>
        <Feather 
          name={isCollapsed ? 'chevron-right' : 'chevron-left'} 
          size={18} 
          color="#6B7280" 
        />
      </Pressable>

      {/* User Profile Section */}
      <View style={styles.profileSection}>
        <View style={styles.avatar}>
          <Text style={styles.avatarText}>JD</Text>
        </View>
        {!isCollapsed && (
          <Animated.View style={styles.profileInfo}>
            <Text style={styles.userName}>John Doe</Text>
            <Text style={styles.userRole}>Student</Text>
          </Animated.View>
        )}
      </View>

      {/* Primary Navigation */}
      <View style={styles.navSection}>
        <NavItem
          iconName="award"
          label="My Courses"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'courses'}
          onPress={() => setActiveItem('courses')}
        />
        <NavItem
          iconName="play-circle"
          label="Learning Path"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'learning-path'}
          onPress={() => setActiveItem('learning-path')}
        />
        <NavItem
          iconName="bookmark"
          label="Saved"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'saved'}
          onPress={() => setActiveItem('saved')}
        />
      </View>

      {/* Secondary Navigation */}
      <View style={styles.secondaryNav}>
        <NavItem
          iconName="settings"
          label="Settings"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'settings'}
          onPress={() => setActiveItem('settings')}
        />
        <NavItem
          iconName="help-circle"
          label="Help Center"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'help'}
          onPress={() => setActiveItem('help')}
        />
      </View>
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    backgroundColor: 'white',
    height: '100%',
    borderRightWidth: 1,
    borderRightColor: '#E5E7EB',
    position: 'relative',
    ...Platform.select({
      web: {
        shadowColor: '#000',
        shadowOffset: { width: 1, height: 0 },
        shadowOpacity: 0.05,
        shadowRadius: 2,
      },
      default: {
        elevation: 2,
      },
    }),
  },
  toggleButton: {
    position: 'absolute',
    right: -10,
    top: 20,
    width: 20,
    height: 20,
    backgroundColor: 'white',
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: '#E5E7EB',
    zIndex: 1,
  },
  profileSection: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
    alignItems: 'center',
  },
  avatar: {
    width: 32,
    height: 32,
    borderRadius: 16,
    backgroundColor: '#7C3AED',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 8,
  },
  avatarText: {
    color: 'white',
    fontSize: 12,
    fontWeight: 'bold',
  },
  profileInfo: {
    alignItems: 'center',
  },
  userName: {
    fontSize: 14,
    fontWeight: '600',
    color: '#1F2937',
    marginBottom: 2,
  },
  userRole: {
    fontSize: 12,
    color: '#6B7280',
  },
  navSection: {
    padding: 8,
  },
  navItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 8,
    borderRadius: 6,
    marginBottom: 2,
  },
  navItemActive: {
    backgroundColor: '#F3F4F6',
  },
  navIcon: {
    width: 24,
    height: 24,
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 12,
    borderRadius: 6,
  },
  navIconActive: {
    backgroundColor: '#EDE9FE',
  },
  navLabel: {
    fontSize: 13,
    color: '#4B5563',
  },
  navLabelActive: {
    color: '#7C3AED',
    fontWeight: '600',
  },
  secondaryNav: {
    marginTop: 'auto',
    padding: 8,
    borderTopWidth: 1,
    borderTopColor: '#E5E7EB',
  },
});
