import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  useWindowDimensions,
  Platform,
  ScrollView,
  Pressable,
} from 'react-native';
import { VideoPlayer } from '../components/video/VideoPlayer';
import { LessonListSidebar } from '../components/course/LessonListSidebar';
import { CourseResources } from '../components/course/CourseResources';
import { LessonNotes } from '../components/course/LessonNotes';
import { CourseTabs } from '../components/course/CourseTabs';
import { Feather } from '@expo/vector-icons';

// Mock data - Replace with API calls
const mockCourse = {
  id: '1',
  title: 'Cybersecurity Fundamentals',
  modules: [
    {
      id: 'm1',
      title: 'Introduction to Cybersecurity',
      progress: 75,
      lessons: [
        {
          id: 'l1',
          title: 'What is Cybersecurity?',
          duration: '15:00',
          completed: true,
          videoUrl: 'https://example.com/video1.mp4',
          description: 'Learn about the basics of cybersecurity in this comprehensive lesson.',
        },
        {
          id: 'l2',
          title: 'Common Security Threats',
          duration: '20:00',
          completed: false,
          videoUrl: 'https://example.com/video2.mp4',
          description: 'Understand the common security threats and how to mitigate them.',
        },
      ],
    },
    {
      id: 'm2',
      title: 'Network Security',
      progress: 25,
      lessons: [
        {
          id: 'l3',
          title: 'Understanding Network Protocols',
          duration: '25:00',
          completed: false,
          videoUrl: 'https://example.com/video3.mp4',
          description: 'Learn about the network protocols and how they work.',
        },
      ],
    },
  ],
  resourceCategories: [
    {
      id: 'cat1',
      title: 'Course Materials',
      resources: [
        {
          id: 'r1',
          title: 'Cybersecurity Basics PDF',
          type: 'pdf',
          url: 'https://example.com/cyber-basics.pdf',
          size: '2.5 MB',
          description: 'Comprehensive guide to cybersecurity fundamentals',
        },
        {
          id: 'r2',
          title: 'Network Diagram',
          type: 'image',
          url: 'https://example.com/network.png',
          size: '500 KB',
          preview: 'https://picsum.photos/200/300',
          description: 'Visual representation of network architecture',
        },
      ],
    },
    {
      id: 'cat2',
      title: 'Additional Resources',
      resources: [
        {
          id: 'r3',
          title: 'Security Tools Setup Guide',
          type: 'pdf',
          url: 'https://example.com/setup-guide.pdf',
          size: '1.8 MB',
          description: 'Step-by-step guide for setting up security tools',
        },
        {
          id: 'r4',
          title: 'Practice Code Examples',
          type: 'code',
          url: 'https://example.com/code-examples.zip',
          size: '1.2 MB',
          description: 'Sample code for security implementations',
        },
      ],
    },
  ],
};

type Tab = 'lessons' | 'resources' | 'notes' | 'qa';

export const CoursePlayerScreen = () => {
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const [currentLessonId, setCurrentLessonId] = useState('l1');
  const [activeTab, setActiveTab] = useState<Tab>('lessons');
  const [notes, setNotes] = useState<Array<{ id: string; content: string; timestamp: number }>>([]);
  const [showSidebar, setShowSidebar] = useState(!isMobile);
  const [currentTime, setCurrentTime] = useState(0);

  const currentLesson = mockCourse.modules
    .flatMap(m => m.lessons)
    .find(l => l.id === currentLessonId);

  const handleLessonComplete = async () => {
    // TODO: Implement API call to mark lesson as complete
    console.log('Lesson completed:', currentLessonId);
  };

  const handleAddNote = (note: { content: string; timestamp: number }) => {
    setNotes(prev => [
      ...prev,
      {
        id: Date.now().toString(),
        ...note,
      },
    ]);
  };

  const handleUpdateNote = (updatedNote: { id: string; content: string; timestamp: number }) => {
    setNotes(prev => prev.map(note => 
      note.id === updatedNote.id ? updatedNote : note
    ));
  };

  const handleDeleteNote = (noteId: string) => {
    setNotes(prev => prev.filter(note => note.id !== noteId));
  };

  const handleExportNotes = () => {
    // TODO: Implement notes export functionality
    console.log('Exporting notes');
  };

  const handleProgress = async (progress: number) => {
    // TODO: Implement API call to save progress
    console.log('Progress:', progress);
  };

  const handleDownloadResource = (resource: any) => {
    // TODO: Implement resource download
    console.log('Downloading resource:', resource);
  };

  const handleDownloadAllResources = () => {
    // TODO: Implement download all resources
    console.log('Downloading all resources');
  };

  const renderSideContent = () => {
    switch (activeTab) {
      case 'lessons':
        return (
          <LessonListSidebar
            modules={mockCourse.modules}
            currentLessonId={currentLessonId}
            lastWatchedLessonId={currentLessonId}
            onLessonSelect={setCurrentLessonId}
            onResumeLastWatched={() => {}}
          />
        );
      case 'resources':
        return (
          <CourseResources
            categories={mockCourse.resourceCategories}
            onDownload={handleDownloadResource}
            onDownloadAll={handleDownloadAllResources}
          />
        );
      case 'notes':
        return (
          <LessonNotes
            notes={notes}
            currentTime={currentTime}
            onAddNote={handleAddNote}
            onUpdateNote={handleUpdateNote}
            onDeleteNote={handleDeleteNote}
            onExportNotes={handleExportNotes}
            onJumpToTimestamp={(timestamp) => {
              // TODO: Implement jump to timestamp
              console.log('Jumping to timestamp:', timestamp);
            }}
          />
        );
      case 'qa':
        return (
          <View style={styles.qaPlaceholder}>
            <Text style={styles.qaText}>Q&A Coming Soon</Text>
          </View>
        );
      default:
        return null;
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.content}>
        <View style={styles.mainContent}>
          {currentLesson && (
            <>
              <View style={styles.playerContainer}>
                <VideoPlayer
                  videoUrl={currentLesson.videoUrl}
                  onProgress={handleProgress}
                  onComplete={handleLessonComplete}
                  onTimeUpdate={setCurrentTime}
                />
              </View>
              <View style={styles.lessonDetails}>
                <View style={styles.lessonHeader}>
                  <View style={styles.lessonInfo}>
                    <Text style={styles.lessonTitle}>{currentLesson.title}</Text>
                    <Text style={styles.lessonDuration}>
                      <Feather name="clock" size={14} color="#6B7280" style={styles.durationIcon} />
                      {currentLesson.duration}
                    </Text>
                  </View>
                  <Pressable
                    style={[styles.completeButton, currentLesson.completed && styles.completedButton]}
                    onPress={handleLessonComplete}
                  >
                    <Feather 
                      name={currentLesson.completed ? "check-circle" : "circle"} 
                      size={18} 
                      color={currentLesson.completed ? "#047857" : "#6B7280"} 
                    />
                    <Text style={[styles.completeButtonText, currentLesson.completed && styles.completedButtonText]}>
                      {currentLesson.completed ? "Completed" : "Mark as Complete"}
                    </Text>
                  </Pressable>
                </View>
                <View style={styles.lessonDescription}>
                  <Text style={styles.descriptionTitle}>About this lesson</Text>
                  <Text style={styles.descriptionText}>
                    {currentLesson.description || "Learn about " + currentLesson.title + " in this comprehensive lesson."}
                  </Text>
                </View>
              </View>
            </>
          )}
        </View>
        <View style={[styles.sidebar, !showSidebar && styles.sidebarHidden]}>
          <CourseTabs currentTab={activeTab} onTabChange={setActiveTab} />
          {renderSideContent()}
        </View>
        {isMobile && (
          <Pressable
            style={styles.toggleButton}
            onPress={() => setShowSidebar(!showSidebar)}
          >
            <Feather
              name={showSidebar ? 'chevron-right' : 'chevron-left'}
              size={24}
              color="#6B7280"
            />
          </Pressable>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  content: {
    flex: 1,
    flexDirection: 'row',
  },
  mainContent: {
    flex: 1,
    padding: 24,
  },
  playerContainer: {
    aspectRatio: 16 / 9,
    backgroundColor: '#000',
    borderRadius: 12,
    overflow: 'hidden',
    marginBottom: 24,
  },
  lessonDetails: {
    backgroundColor: 'white',
    borderRadius: 12,
    padding: 24,
    marginBottom: 24,
    borderWidth: 1,
    borderColor: '#E5E7EB',
  },
  lessonHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 16,
  },
  lessonInfo: {
    flex: 1,
    marginRight: 16,
  },
  lessonTitle: {
    fontSize: 24,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 8,
  },
  lessonDuration: {
    fontSize: 14,
    color: '#6B7280',
    flexDirection: 'row',
    alignItems: 'center',
  },
  durationIcon: {
    marginRight: 6,
  },
  completeButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#F3F4F6',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#E5E7EB',
  },
  completedButton: {
    backgroundColor: '#ECFDF5',
    borderColor: '#A7F3D0',
  },
  completeButtonText: {
    marginLeft: 8,
    fontSize: 14,
    fontWeight: '500',
    color: '#6B7280',
  },
  completedButtonText: {
    color: '#047857',
  },
  lessonDescription: {
    borderTopWidth: 1,
    borderTopColor: '#E5E7EB',
    paddingTop: 16,
  },
  descriptionTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#374151',
    marginBottom: 8,
  },
  descriptionText: {
    fontSize: 14,
    lineHeight: 20,
    color: '#6B7280',
  },
  sidebar: {
    width: Platform.OS === 'web' ? 400 : '100%',
    backgroundColor: 'white',
    borderLeftWidth: Platform.OS === 'web' ? 1 : 0,
    borderLeftColor: '#E5E7EB',
  },
  sidebarHidden: {
    display: 'none',
  },
  toggleButton: {
    position: 'absolute',
    right: 0,
    top: '50%',
    backgroundColor: 'white',
    padding: 8,
    borderTopLeftRadius: 8,
    borderBottomLeftRadius: 8,
    borderWidth: 1,
    borderRightWidth: 0,
    borderColor: '#E5E7EB',
  },
  qaPlaceholder: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 24,
  },
  qaText: {
    fontSize: 16,
    color: '#6B7280',
    fontWeight: '500',
  },
});
