import React, { useEffect, useState } from 'react';
import { ScrollView, View, Text, StyleSheet, Platform } from 'react-native';
import { CourseCard } from '../components/course/CourseCard';
import { fetchCourses } from '../services/strapi';

export const HomeScreen = ({ navigation }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadCourses = async () => {
      try {
        const coursesData = await fetchCourses();
        setCourses(coursesData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadCourses();
  }, []);

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text>Loading courses...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>Error: {error}</Text>
      </View>
    );
  }

  return (
    <ScrollView 
      style={styles.container}
      contentContainerStyle={styles.contentContainer}
      showsVerticalScrollIndicator={false}
    >
      <View style={styles.header}>
        <Text style={styles.welcomeText}>Welcome to SKiddy</Text>
        <Text style={styles.subText}>Start your learning journey today</Text>
      </View>

      <View style={styles.coursesSection}>
        <Text style={styles.sectionTitle}>Featured Courses</Text>
        <View style={styles.courseGrid}>
          {courses.map((course) => (
            <View key={course.id} style={styles.cardWrapper}>
              <CourseCard
                title={course.attributes.title}
                category={course.attributes.category}
                duration={course.attributes.duration}
                rating={course.attributes.rating}
                thumbnail={course.attributes.thumbnail?.data?.attributes?.url}
                instructor={course.attributes.instructor}
                onPress={() => navigation.navigate('CoursePlayer', { courseId: course.id })}
              />
            </View>
          ))}
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  centerContent: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  contentContainer: {
    padding: 24,
  },
  header: {
    marginBottom: 32,
  },
  welcomeText: {
    fontSize: 32,
    fontWeight: '700',
    color: '#111827',
    marginBottom: 8,
  },
  subText: {
    fontSize: 18,
    color: '#6B7280',
  },
  coursesSection: {
    marginBottom: 32,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 24,
  },
  courseGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: 24,
    justifyContent: Platform.OS === 'web' ? 'flex-start' : 'center',
  },
  cardWrapper: {
    flex: Platform.OS === 'web' ? 1 : undefined,
    minWidth: Platform.OS === 'web' ? 300 : undefined,
    maxWidth: Platform.OS === 'web' ? 400 : 340,
  },
  errorText: {
    color: '#EF4444',
    fontSize: 16,
  },
});
