import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, FlatList, Pressable, useWindowDimensions, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { LearningPathCard } from '../components/learning-path/LearningPathCard';

// Mock data for learning paths
const mockLearningPaths = [
  {
    id: '1',
    title: 'Full-Stack Web Developer',
    description: 'Master both front-end and back-end development with this comprehensive path covering HTML, CSS, JavaScript, React, Node.js, and more.',
    totalCourses: 12,
    completedCourses: 3,
    estimatedTime: '6 months',
    difficulty: 'Intermediate',
    category: 'Web Development',
    milestones: [
      { title: 'Front-End Basics', completed: true },
      { title: 'JavaScript Mastery', completed: true },
      { title: 'React Development', completed: true },
      { title: 'Backend Development', completed: false },
      { title: 'Database Management', completed: false },
    ],
  },
  {
    id: '2',
    title: 'Data Science Specialist',
    description: 'Learn data analysis, machine learning, and statistical modeling. Perfect for aspiring data scientists and analysts.',
    totalCourses: 8,
    completedCourses: 0,
    estimatedTime: '4 months',
    difficulty: 'Advanced',
    category: 'Data Science',
    milestones: [
      { title: 'Python for Data Science', completed: false },
      { title: 'Statistical Analysis', completed: false },
      { title: 'Machine Learning', completed: false },
      { title: 'Deep Learning', completed: false },
    ],
  },
  {
    id: '3',
    title: 'Mobile App Developer',
    description: 'Build cross-platform mobile applications using React Native. Learn mobile UI/UX, state management, and app deployment.',
    totalCourses: 6,
    completedCourses: 1,
    estimatedTime: '3 months',
    difficulty: 'Beginner',
    category: 'Mobile Development',
    milestones: [
      { title: 'React Native Basics', completed: true },
      { title: 'Mobile UI Components', completed: false },
      { title: 'App State Management', completed: false },
      { title: 'App Deployment', completed: false },
    ],
  },
];

const categories = ['All Paths', 'Web Development', 'Data Science', 'Mobile Development', 'Cloud Computing'];

export const LearningPathScreen = ({ navigation }) => {
  const [selectedCategory, setSelectedCategory] = useState('All Paths');
  const { width } = useWindowDimensions();
  const [paths, setPaths] = useState(mockLearningPaths);

  const getNumColumns = () => {
    if (width < 768) return 1;
    if (width < 1200) return 2;
    return 3;
  };

  const filteredPaths = selectedCategory === 'All Paths'
    ? paths
    : paths.filter(path => path.category === selectedCategory);

  const renderPath = ({ item }) => (
    <View style={styles.cardWrapper}>
      <LearningPathCard
        {...item}
        onPress={() => navigation.navigate('LearningPathDetails', { pathId: item.id })}
      />
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <View>
          <Text style={styles.title}>Learning Paths</Text>
          <Text style={styles.subtitle}>Choose a learning path to achieve your career goals</Text>
        </View>
        <Pressable
          style={styles.createButton}
          onPress={() => navigation.navigate('CreateLearningPath')}
        >
          <Feather name="plus" size={20} color="white" />
          <Text style={styles.createButtonText}>Create Custom Path</Text>
        </Pressable>
      </View>

      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        style={styles.categoriesContainer}
      >
        {categories.map(category => (
          <Pressable
            key={category}
            style={[
              styles.categoryButton,
              selectedCategory === category && styles.categoryButtonActive
            ]}
            onPress={() => setSelectedCategory(category)}
          >
            <Text
              style={[
                styles.categoryButtonText,
                selectedCategory === category && styles.categoryButtonTextActive
              ]}
            >
              {category}
            </Text>
          </Pressable>
        ))}
      </ScrollView>

      <FlatList
        data={filteredPaths}
        renderItem={renderPath}
        keyExtractor={item => item.id}
        numColumns={getNumColumns()}
        columnWrapperStyle={width >= 768 ? styles.pathsGrid : undefined}
        contentContainerStyle={styles.pathsContainer}
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  header: {
    padding: 24,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: '700',
    color: '#1F2937',
    marginBottom: 4,
  },
  subtitle: {
    fontSize: 16,
    color: '#6B7280',
  },
  createButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#7C3AED',
    paddingHorizontal: 16,
    paddingVertical: 10,
    borderRadius: 8,
    boxShadow: Platform.OS === 'web' ? '0 2px 4px rgba(124, 58, 237, 0.2)' : undefined,
    elevation: Platform.OS === 'android' ? 2 : undefined,
  },
  createButtonText: {
    color: 'white',
    fontSize: 14,
    fontWeight: '600',
    marginLeft: 8,
  },
  categoriesContainer: {
    paddingHorizontal: 24,
    marginBottom: 24,
  },
  categoryButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
    backgroundColor: '#F3F4F6',
    marginRight: 8,
  },
  categoryButtonActive: {
    backgroundColor: '#7C3AED',
  },
  categoryButtonText: {
    fontSize: 14,
    fontWeight: '500',
    color: '#6B7280',
  },
  categoryButtonTextActive: {
    color: 'white',
  },
  pathsContainer: {
    padding: 24,
    paddingTop: 0,
  },
  cardWrapper: {
    flex: 1,
    padding: 12,
  },
  pathsGrid: {
    justifyContent: 'flex-start',
    gap: 24,
  },
});
