import React, { useState, useRef, useEffect } from 'react';
import { View, Text, StyleSheet, Pressable, Platform } from 'react-native';
import { Video, ResizeMode } from 'expo-av';
import { Feather } from '@expo/vector-icons';
import * as ScreenOrientation from 'expo-screen-orientation';

interface VideoPlayerProps {
  videoUrl: string;
  onComplete?: () => void;
  onProgress?: (progress: number) => void;
  onTimeUpdate?: (time: number) => void;
  initialProgress?: number;
}

export const VideoPlayer = ({
  videoUrl,
  onComplete,
  onProgress,
  onTimeUpdate,
  initialProgress = 0,
}: VideoPlayerProps) => {
  const videoRef = useRef<Video>(null);
  const [status, setStatus] = useState<any>({});
  const [showControls, setShowControls] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [playbackSpeed, setPlaybackSpeed] = useState(1.0);
  const controlsTimeoutRef = useRef<NodeJS.Timeout>();

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.setPositionAsync(initialProgress * 1000);
    }
  }, [initialProgress]);

  useEffect(() => {
    return () => {
      if (controlsTimeoutRef.current) {
        clearTimeout(controlsTimeoutRef.current);
      }
    };
  }, []);

  const handlePlaybackStatusUpdate = (status: any) => {
    setStatus(status);
    if (status.didJustFinish && onComplete) {
      onComplete();
    }
    if (status.positionMillis && status.durationMillis && onProgress) {
      onProgress(status.positionMillis / status.durationMillis);
    }
    if (status.positionMillis && onTimeUpdate) {
      onTimeUpdate(status.positionMillis / 1000); // Convert to seconds
    }
  };

  const togglePlay = async () => {
    if (videoRef.current) {
      if (status.isPlaying) {
        await videoRef.current.pauseAsync();
      } else {
        await videoRef.current.playAsync();
      }
    }
  };

  const handleSeek = async (forward: boolean) => {
    if (videoRef.current && status.positionMillis) {
      const seekAmount = 10000; // 10 seconds
      const newPosition = status.positionMillis + (forward ? seekAmount : -seekAmount);
      await videoRef.current.setPositionAsync(Math.max(0, newPosition));
    }
  };

  const toggleFullscreen = async () => {
    if (Platform.OS !== 'web') {
      if (isFullscreen) {
        await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.PORTRAIT);
      } else {
        await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.LANDSCAPE);
      }
    }
    setIsFullscreen(!isFullscreen);
  };

  const formatTime = (milliseconds: number) => {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  const handleControlsShow = () => {
    setShowControls(true);
    if (controlsTimeoutRef.current) {
      clearTimeout(controlsTimeoutRef.current);
    }
    if (status.isPlaying) {
      controlsTimeoutRef.current = setTimeout(() => {
        setShowControls(false);
      }, 3000);
    }
  };

  const handleProgressBarPress = (event: any) => {
    if (videoRef.current && status.durationMillis) {
      const progressBarWidth = event.currentTarget.offsetWidth;
      const clickPosition = event.nativeEvent.offsetX;
      const percentage = clickPosition / progressBarWidth;
      const newPosition = status.durationMillis * percentage;
      videoRef.current.setPositionAsync(newPosition);
    }
  };

  return (
    <View 
      style={[styles.container, isFullscreen && styles.fullscreen]}
      onTouchStart={handleControlsShow}
    >
      <Video
        ref={videoRef}
        style={styles.video}
        source={{ uri: videoUrl }}
        useNativeControls={Platform.OS !== 'web'}
        resizeMode={ResizeMode.CONTAIN}
        onPlaybackStatusUpdate={handlePlaybackStatusUpdate}
        isMuted={false}
        volume={1.0}
        rate={playbackSpeed}
      />

      {showControls && Platform.OS === 'web' && (
        <View style={styles.controls}>
          <Pressable style={styles.progressBarContainer} onPress={handleProgressBarPress}>
            <View style={styles.progressBar}>
              <View
                style={[
                  styles.progressFill,
                  {
                    width: `${
                      (status.positionMillis / status.durationMillis) * 100 || 0
                    }%`,
                  },
                ]}
              />
            </View>
          </Pressable>

          <View style={styles.controlsRow}>
            <View style={styles.leftControls}>
              <Pressable onPress={togglePlay} style={styles.controlButton}>
                <Feather
                  name={status.isPlaying ? 'pause' : 'play'}
                  size={24}
                  color="white"
                />
              </Pressable>

              <Pressable onPress={() => handleSeek(false)} style={styles.controlButton}>
                <Feather name="rewind" size={20} color="white" />
              </Pressable>

              <Pressable onPress={() => handleSeek(true)} style={styles.controlButton}>
                <Feather name="fast-forward" size={20} color="white" />
              </Pressable>

              <Text style={styles.timeText}>
                {formatTime(status.positionMillis || 0)} /{' '}
                {formatTime(status.durationMillis || 0)}
              </Text>
            </View>

            <View style={styles.rightControls}>
              <Pressable
                onPress={() => setPlaybackSpeed(speed => (speed === 1 ? 1.5 : 1))}
                style={styles.controlButton}
              >
                <Text style={styles.speedText}>{playbackSpeed}x</Text>
              </Pressable>

              <Pressable onPress={toggleFullscreen} style={styles.controlButton}>
                <Feather
                  name={isFullscreen ? 'minimize' : 'maximize'}
                  size={20}
                  color="white"
                />
              </Pressable>
            </View>
          </View>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000',
    borderRadius: 12,
    overflow: 'hidden',
    position: 'relative',
  },
  fullscreen: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 999,
    backgroundColor: '#000',
    borderRadius: 0,
  },
  video: {
    width: '100%',
    aspectRatio: 16 / 9,
  },
  controls: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    padding: 16,
  },
  progressBarContainer: {
    width: '100%',
    height: 20,
    justifyContent: 'center',
    cursor: 'pointer',
  },
  progressBar: {
    height: 4,
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    borderRadius: 2,
  },
  progressFill: {
    height: '100%',
    backgroundColor: '#7C3AED',
    borderRadius: 2,
  },
  controlsRow: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginTop: 8,
  },
  leftControls: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  rightControls: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  controlButton: {
    marginRight: 16,
    padding: 8,
    borderRadius: 4,
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
  },
  timeText: {
    color: 'white',
    fontSize: 14,
    marginLeft: 8,
  },
  speedText: {
    color: 'white',
    fontSize: 14,
    fontWeight: '500',
  },
});
