import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, TextInput, Pressable, FlatList, useWindowDimensions, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { CourseCard } from '../components/course/CourseCard';

// Mock data for enrolled courses
const mockEnrolledCourses = [
  {
    id: '1',
    title: 'Introduction to React Native',
    category: 'Mobile Development',
    duration: '6h 30m',
    rating: 4.8,
    thumbnail: 'https://picsum.photos/800/600',
    instructor: 'Sarah Johnson',
    progress: 45,
    status: 'in_progress',
    lastAccessedLesson: 'Building Your First Component',
    lastAccessed: new Date('2024-01-15'),
  },
  {
    id: '2',
    title: 'Advanced JavaScript Concepts',
    category: 'Web Development',
    duration: '8h 45m',
    rating: 4.9,
    thumbnail: 'https://picsum.photos/800/600?random=2',
    instructor: 'Michael Chen',
    progress: 100,
    status: 'completed',
    lastAccessedLesson: 'Advanced Promise Patterns',
    lastAccessed: new Date('2024-01-10'),
  },
  {
    id: '3',
    title: 'Python for Data Science',
    category: 'Data Science',
    duration: '10h 15m',
    rating: 4.7,
    thumbnail: 'https://picsum.photos/800/600?random=3',
    instructor: 'Emily Rodriguez',
    progress: 0,
    status: 'upcoming',
    lastAccessed: new Date('2024-01-05'),
  },
  {
    id: '4',
    title: 'Cybersecurity Fundamentals',
    category: 'Cybersecurity',
    duration: '8h 15m',
    rating: 4.9,
    thumbnail: 'https://picsum.photos/800/600?random=4',
    instructor: 'Alex Thompson',
    progress: 0,
    status: 'upcoming',
    description: 'Learn the fundamentals of cybersecurity, including network security, cryptography, and ethical hacking basics.',
    topics: [
      'Introduction to Cybersecurity',
      'Network Security Fundamentals',
      'Basic Cryptography',
      'Security Tools and Practices',
      'Ethical Hacking Introduction',
      'Security Policies and Procedures'
    ],
    lastAccessed: new Date(),
  }
];

const categories = ['All', 'Cybersecurity', 'Web Development', 'Mobile Development', 'Data Science'];
const sortOptions = [
  { label: 'Recently Accessed', value: 'recent' },
  { label: 'Progress', value: 'progress' },
  { label: 'Duration', value: 'duration' },
];

export const MyCoursesScreen = ({ navigation }) => {
  const [courses, setCourses] = useState(mockEnrolledCourses);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortBy, setSortBy] = useState('recent');
  const [searchQuery, setSearchQuery] = useState('');
  const { width } = useWindowDimensions();

  // Calculate number of columns based on screen width
  const getNumColumns = () => {
    if (width < 768) return 1;
    if (width < 1024) return 2;
    return 3;
  };

  // Filter and sort courses
  useEffect(() => {
    let filtered = [...mockEnrolledCourses];

    // Apply category filter
    if (selectedCategory !== 'All') {
      filtered = filtered.filter(course => course.category === selectedCategory);
    }

    // Apply search filter
    if (searchQuery) {
      filtered = filtered.filter(course => 
        course.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        course.instructor.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Apply sorting
    switch (sortBy) {
      case 'recent':
        filtered.sort((a, b) => b.lastAccessed.getTime() - a.lastAccessed.getTime());
        break;
      case 'progress':
        filtered.sort((a, b) => b.progress - a.progress);
        break;
      case 'duration':
        filtered.sort((a, b) => {
          const getDuration = (str) => parseInt(str.split('h')[0]);
          return getDuration(b.duration) - getDuration(a.duration);
        });
        break;
    }

    setCourses(filtered);
  }, [selectedCategory, sortBy, searchQuery]);

  const renderCourseCard = ({ item }) => (
    <View style={styles.cardWrapper}>
      <CourseCard
        {...item}
        onPress={() => navigation.navigate('CourseDetails', { courseId: item.id })}
        onResume={() => navigation.navigate('CourseLesson', { 
          courseId: item.id, 
          lessonId: 'last-accessed'
        })}
        onUnenroll={() => {
          // Handle unenroll action
          console.log('Unenroll from course:', item.id);
        }}
      />
    </View>
  );

  return (
    <View style={styles.container}>
      {/* Header Section */}
      <View style={styles.header}>
        <Text style={styles.title}>My Courses</Text>
        <View style={styles.searchContainer}>
          <Feather name="search" size={20} color="#6B7280" />
          <TextInput
            style={styles.searchInput}
            placeholder="Search courses..."
            value={searchQuery}
            onChangeText={setSearchQuery}
          />
        </View>
      </View>

      {/* Filters Section */}
      <View style={styles.filtersContainer}>
        <ScrollView 
          horizontal 
          showsHorizontalScrollIndicator={false}
          style={styles.categoriesScroll}
        >
          {categories.map(category => (
            <Pressable
              key={category}
              style={[
                styles.categoryButton,
                selectedCategory === category && styles.categoryButtonActive
              ]}
              onPress={() => setSelectedCategory(category)}
            >
              <Text
                style={[
                  styles.categoryButtonText,
                  selectedCategory === category && styles.categoryButtonTextActive
                ]}
              >
                {category}
              </Text>
            </Pressable>
          ))}
        </ScrollView>

        <View style={styles.sortContainer}>
          <Text style={styles.sortLabel}>Sort by:</Text>
          <View style={styles.sortButtons}>
            {sortOptions.map(option => (
              <Pressable
                key={option.value}
                style={[
                  styles.sortButton,
                  sortBy === option.value && styles.sortButtonActive
                ]}
                onPress={() => setSortBy(option.value)}
              >
                <Text
                  style={[
                    styles.sortButtonText,
                    sortBy === option.value && styles.sortButtonTextActive
                  ]}
                >
                  {option.label}
                </Text>
              </Pressable>
            ))}
          </View>
        </View>
      </View>

      {/* Courses Grid */}
      <FlatList
        data={courses}
        renderItem={renderCourseCard}
        keyExtractor={item => item.id}
        numColumns={getNumColumns()}
        columnWrapperStyle={width >= 768 ? styles.courseGrid : undefined}
        contentContainerStyle={styles.coursesContainer}
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  header: {
    padding: 24,
    paddingBottom: 0,
  },
  title: {
    fontSize: 24,
    fontWeight: '700',
    color: '#1F2937',
    marginBottom: 16,
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 12,
    padding: 12,
    marginBottom: 24,
    boxShadow: Platform.OS === 'web' ? '0 1px 2px rgba(0, 0, 0, 0.05)' : undefined,
    elevation: Platform.OS === 'android' ? 2 : undefined,
  },
  searchInput: {
    flex: 1,
    marginLeft: 12,
    fontSize: 16,
    color: '#1F2937',
  },
  filtersContainer: {
    paddingHorizontal: 24,
    marginBottom: 24,
  },
  categoriesScroll: {
    marginBottom: 16,
  },
  categoryButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
    backgroundColor: '#F3F4F6',
    marginRight: 8,
  },
  categoryButtonActive: {
    backgroundColor: '#7C3AED',
  },
  categoryButtonText: {
    fontSize: 14,
    fontWeight: '500',
    color: '#6B7280',
  },
  categoryButtonTextActive: {
    color: 'white',
  },
  sortContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  sortLabel: {
    fontSize: 14,
    color: '#6B7280',
    marginRight: 12,
  },
  sortButtons: {
    flexDirection: 'row',
  },
  sortButton: {
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 6,
    marginRight: 8,
  },
  sortButtonActive: {
    backgroundColor: '#F5F3FF',
  },
  sortButtonText: {
    fontSize: 14,
    color: '#6B7280',
  },
  sortButtonTextActive: {
    color: '#7C3AED',
    fontWeight: '500',
  },
  coursesContainer: {
    padding: 24,
    paddingTop: 0,
  },
  cardWrapper: {
    flex: 1,
    maxWidth: Platform.OS === 'web' ? 340 : undefined,
    marginHorizontal: 12,
    marginBottom: 24,
  },
  courseGrid: {
    justifyContent: 'flex-start',
    gap: 24,
  },
});
