import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  useWindowDimensions,
  Platform,
  ScrollView,
  Pressable,
} from 'react-native';
import { VideoPlayer } from '../components/video/VideoPlayer';
import { LessonList } from '../components/course/LessonList';
import { CourseResources } from '../components/course/CourseResources';
import { LessonNotes } from '../components/course/LessonNotes';
import { Feather } from '@expo/vector-icons';

// Mock data - Replace with API calls
const mockCourse = {
  id: '1',
  title: 'Cybersecurity Fundamentals',
  modules: [
    {
      id: 'm1',
      title: 'Introduction to Cybersecurity',
      lessons: [
        {
          id: 'l1',
          title: 'What is Cybersecurity?',
          duration: 15,
          completed: true,
          videoUrl: 'https://example.com/video1.mp4',
        },
        {
          id: 'l2',
          title: 'Common Security Threats',
          duration: 20,
          completed: false,
          videoUrl: 'https://example.com/video2.mp4',
        },
      ],
    },
    {
      id: 'm2',
      title: 'Network Security',
      lessons: [
        {
          id: 'l3',
          title: 'Understanding Network Protocols',
          duration: 25,
          completed: false,
          videoUrl: 'https://example.com/video3.mp4',
        },
        {
          id: 'l4',
          title: 'Firewall Configuration',
          duration: 30,
          completed: false,
          videoUrl: 'https://example.com/video4.mp4',
        },
      ],
    },
  ],
  resources: [
    {
      id: 'r1',
      title: 'Course Syllabus',
      type: 'pdf',
      url: 'https://example.com/syllabus.pdf',
      size: '2.4 MB',
    },
    {
      id: 'r2',
      title: 'Security Tools Setup Guide',
      type: 'code',
      url: 'https://example.com/setup-guide.zip',
      size: '1.8 MB',
    },
    {
      id: 'r3',
      title: 'Additional Reading',
      type: 'link',
      url: 'https://example.com/reading',
    },
  ],
};

type Tab = 'lessons' | 'resources' | 'notes';

export const CoursePlayerScreen = () => {
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const [currentLessonId, setCurrentLessonId] = useState('l1');
  const [activeTab, setActiveTab] = useState<Tab>('lessons');
  const [notes, setNotes] = useState<Array<{ id: string; content: string; timestamp: number }>>([]);
  const [showSidebar, setShowSidebar] = useState(!isMobile);

  const currentLesson = mockCourse.modules
    .flatMap(m => m.lessons)
    .find(l => l.id === currentLessonId);

  const handleLessonComplete = async () => {
    // TODO: Implement API call to mark lesson as complete
    console.log('Lesson completed:', currentLessonId);
  };

  const handleSaveNote = (note: { content: string; timestamp: number }) => {
    setNotes(prev => [
      ...prev,
      {
        id: Date.now().toString(),
        ...note,
      },
    ]);
  };

  const handleProgress = async (progress: number) => {
    // TODO: Implement API call to save progress
    console.log('Progress:', progress);
  };

  const renderSideContent = () => {
    switch (activeTab) {
      case 'lessons':
        return (
          <LessonList
            modules={mockCourse.modules}
            currentLessonId={currentLessonId}
            onLessonSelect={setCurrentLessonId}
          />
        );
      case 'resources':
        return <CourseResources resources={mockCourse.resources} />;
      case 'notes':
        return (
          <LessonNotes
            lessonId={currentLessonId}
            onSaveNote={handleSaveNote}
            notes={notes}
          />
        );
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.content}>
        <View style={styles.mainContent}>
          {currentLesson && (
            <View style={styles.playerContainer}>
              <VideoPlayer
                videoUrl={currentLesson.videoUrl}
                title={currentLesson.title}
                onComplete={handleLessonComplete}
                onProgress={handleProgress}
              />
              <View style={styles.lessonInfo}>
                <Text style={styles.lessonTitle}>{currentLesson.title}</Text>
                <Pressable
                  style={styles.completeButton}
                  onPress={handleLessonComplete}
                >
                  <Feather name="check-circle" size={20} color="white" />
                  <Text style={styles.completeButtonText}>Mark as Complete</Text>
                </Pressable>
              </View>
            </View>
          )}

          {isMobile && (
            <View style={styles.tabBar}>
              <Pressable
                style={[styles.tab, activeTab === 'lessons' && styles.activeTab]}
                onPress={() => setActiveTab('lessons')}
              >
                <Text
                  style={[
                    styles.tabText,
                    activeTab === 'lessons' && styles.activeTabText,
                  ]}
                >
                  Lessons
                </Text>
              </Pressable>
              <Pressable
                style={[styles.tab, activeTab === 'resources' && styles.activeTab]}
                onPress={() => setActiveTab('resources')}
              >
                <Text
                  style={[
                    styles.tabText,
                    activeTab === 'resources' && styles.activeTabText,
                  ]}
                >
                  Resources
                </Text>
              </Pressable>
              <Pressable
                style={[styles.tab, activeTab === 'notes' && styles.activeTab]}
                onPress={() => setActiveTab('notes')}
              >
                <Text
                  style={[
                    styles.tabText,
                    activeTab === 'notes' && styles.activeTabText,
                  ]}
                >
                  Notes
                </Text>
              </Pressable>
            </View>
          )}
        </View>

        {(!isMobile || showSidebar) && (
          <View style={styles.sidebar}>
            {!isMobile && (
              <View style={styles.sidebarTabs}>
                <Pressable
                  style={[
                    styles.sidebarTab,
                    activeTab === 'lessons' && styles.activeSidebarTab,
                  ]}
                  onPress={() => setActiveTab('lessons')}
                >
                  <Feather
                    name="list"
                    size={20}
                    color={activeTab === 'lessons' ? '#7C3AED' : '#6B7280'}
                  />
                  <Text
                    style={[
                      styles.sidebarTabText,
                      activeTab === 'lessons' && styles.activeSidebarTabText,
                    ]}
                  >
                    Lessons
                  </Text>
                </Pressable>
                <Pressable
                  style={[
                    styles.sidebarTab,
                    activeTab === 'resources' && styles.activeSidebarTab,
                  ]}
                  onPress={() => setActiveTab('resources')}
                >
                  <Feather
                    name="file-text"
                    size={20}
                    color={activeTab === 'resources' ? '#7C3AED' : '#6B7280'}
                  />
                  <Text
                    style={[
                      styles.sidebarTabText,
                      activeTab === 'resources' && styles.activeSidebarTabText,
                    ]}
                  >
                    Resources
                  </Text>
                </Pressable>
                <Pressable
                  style={[
                    styles.sidebarTab,
                    activeTab === 'notes' && styles.activeSidebarTab,
                  ]}
                  onPress={() => setActiveTab('notes')}
                >
                  <Feather
                    name="edit-3"
                    size={20}
                    color={activeTab === 'notes' ? '#7C3AED' : '#6B7280'}
                  />
                  <Text
                    style={[
                      styles.sidebarTabText,
                      activeTab === 'notes' && styles.activeSidebarTabText,
                    ]}
                  >
                    Notes
                  </Text>
                </Pressable>
              </View>
            )}
            <View style={styles.sidebarContent}>{renderSideContent()}</View>
          </View>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  content: {
    flex: 1,
    flexDirection: 'row',
  },
  mainContent: {
    flex: 1,
    padding: 24,
  },
  playerContainer: {
    backgroundColor: 'white',
    borderRadius: 12,
    overflow: 'hidden',
    ...Platform.select({
      web: {
        boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
      },
      default: {
        elevation: 2,
      },
    }),
  },
  lessonInfo: {
    padding: 16,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderTopWidth: 1,
    borderTopColor: '#E5E7EB',
  },
  lessonTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: '#111827',
  },
  completeButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#059669',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 6,
  },
  completeButtonText: {
    color: 'white',
    fontSize: 14,
    fontWeight: '500',
    marginLeft: 8,
  },
  sidebar: {
    width: Platform.OS === 'web' ? 360 : '100%',
    backgroundColor: 'white',
    borderLeftWidth: Platform.OS === 'web' ? 1 : 0,
    borderLeftColor: '#E5E7EB',
  },
  sidebarTabs: {
    flexDirection: 'row',
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
  },
  sidebarTab: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    gap: 8,
  },
  activeSidebarTab: {
    borderBottomWidth: 2,
    borderBottomColor: '#7C3AED',
  },
  sidebarTabText: {
    fontSize: 14,
    fontWeight: '500',
    color: '#6B7280',
  },
  activeSidebarTabText: {
    color: '#7C3AED',
  },
  sidebarContent: {
    flex: 1,
  },
  tabBar: {
    flexDirection: 'row',
    backgroundColor: 'white',
    marginTop: 16,
    borderRadius: 8,
    padding: 4,
  },
  tab: {
    flex: 1,
    paddingVertical: 8,
    alignItems: 'center',
  },
  activeTab: {
    backgroundColor: '#F3F4F6',
    borderRadius: 4,
  },
  tabText: {
    fontSize: 14,
    fontWeight: '500',
    color: '#6B7280',
  },
  activeTabText: {
    color: '#111827',
  },
});
