import React from 'react';
import { View, Text, Pressable, StyleSheet } from 'react-native';
import { Home, Book, Graduation, Code, Users, Settings, HelpCircle } from 'lucide-react-native';

const NavItem = ({ icon: Icon, label }: { icon: any, label: string }) => (
  <Pressable style={styles.navItem}>
    <View style={styles.navIcon}>
      <Icon style={styles.icon} />
    </View>
    <Text style={styles.navLabel}>{label}</Text>
  </Pressable>
);

export const Sidebar = () => {
  return (
    <View style={styles.sidebar}>
      {/* User Profile Section */}
      <View style={styles.profileSection}>
        <View style={styles.avatar} />
        <Text style={styles.userName}>John Doe</Text>
        <Text style={styles.userRole}>Student</Text>
      </View>

      {/* Primary Navigation */}
      <View style={styles.navSection}>
        <NavItem icon={Home} label="Dashboard" />
        <NavItem icon={Book} label="eBook" />
        <NavItem icon={Graduation} label="Courses" />
        <NavItem icon={Code} label="Code Challenges" />
        <NavItem icon={Users} label="Community" />
      </View>

      {/* Secondary Navigation */}
      <View style={styles.secondaryNav}>
        <NavItem icon={Settings} label="Settings" />
        <NavItem icon={HelpCircle} label="Support" />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    width: 256,
    backgroundColor: 'white',
    height: '100%',
    borderRightWidth: 1,
    borderRightColor: '#E5E7EB',
  },
  profileSection: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
  },
  avatar: {
    width: 48,
    height: 48,
    borderRadius: 24,
    backgroundColor: '#7C3AED',
    marginBottom: 8,
  },
  userName: {
    fontWeight: 'bold',
    color: '#1F2937',
  },
  userRole: {
    fontSize: 14,
    color: '#6B7280',
  },
  navSection: {
    padding: 8,
  },
  navItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 8,
    borderRadius: 8,
  },
  navIcon: {
    width: 20,
    height: 20,
    marginRight: 12,
  },
  icon: {
    width: 20,
    height: 20,
  },
  navLabel: {
    color: '#1F2937',
  },
  secondaryNav: {
    marginTop: 'auto',
    padding: 8,
    borderTopWidth: 1,
    borderTopColor: '#E5E7EB',
  },
});
