import React, { useState } from 'react';
import { View, Text, Pressable, StyleSheet, useWindowDimensions } from 'react-native';
import { Feather } from '@expo/vector-icons';
import Animated, { 
  useAnimatedStyle, 
  withTiming, 
  withSpring,
  useSharedValue
} from 'react-native-reanimated';

interface NavItemProps {
  iconName: keyof typeof Feather.glyphMap;
  label: string;
  isCollapsed: boolean;
  isActive?: boolean;
  onPress: () => void;
}

const NavItem = ({ iconName, label, isCollapsed, isActive, onPress }: NavItemProps) => (
  <Pressable
    style={[styles.navItem, isActive && styles.navItemActive]}
    onPress={onPress}
  >
    <View style={styles.navIcon}>
      <Feather name={iconName} size={20} color={isActive ? '#7C3AED' : '#1F2937'} />
    </View>
    {!isCollapsed && (
      <Text style={[styles.navLabel, isActive && styles.navLabelActive]}>{label}</Text>
    )}
  </Pressable>
);

export const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [activeItem, setActiveItem] = useState('dashboard');
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const widthValue = useSharedValue(256);

  const sidebarStyle = useAnimatedStyle(() => {
    return {
      width: withSpring(isCollapsed ? 72 : 256, {
        damping: 15,
        stiffness: 100,
      }),
    };
  }, [isCollapsed]);

  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <Animated.View style={[styles.sidebar, sidebarStyle]}>
      {/* Toggle Button */}
      <Pressable style={styles.toggleButton} onPress={toggleCollapse}>
        <Feather 
          name={isCollapsed ? 'chevron-right' : 'chevron-left'} 
          size={20} 
          color="#6B7280" 
        />
      </Pressable>

      {/* User Profile Section */}
      <View style={styles.profileSection}>
        <View style={styles.avatar}>
          <Text style={styles.avatarText}>JD</Text>
        </View>
        {!isCollapsed && (
          <Animated.View>
            <Text style={styles.userName}>John Doe</Text>
            <Text style={styles.userRole}>Student</Text>
          </Animated.View>
        )}
      </View>

      {/* Primary Navigation */}
      <View style={styles.navSection}>
        <NavItem
          iconName="home"
          label="Dashboard"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'dashboard'}
          onPress={() => setActiveItem('dashboard')}
        />
        <NavItem
          iconName="book"
          label="eBook"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'ebook'}
          onPress={() => setActiveItem('ebook')}
        />
        <NavItem
          iconName="award"
          label="Courses"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'courses'}
          onPress={() => setActiveItem('courses')}
        />
        <NavItem
          iconName="code"
          label="Code Challenges"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'challenges'}
          onPress={() => setActiveItem('challenges')}
        />
        <NavItem
          iconName="users"
          label="Community"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'community'}
          onPress={() => setActiveItem('community')}
        />
      </View>

      {/* Secondary Navigation */}
      <View style={styles.secondaryNav}>
        <NavItem
          iconName="settings"
          label="Settings"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'settings'}
          onPress={() => setActiveItem('settings')}
        />
        <NavItem
          iconName="help-circle"
          label="Support"
          isCollapsed={isCollapsed}
          isActive={activeItem === 'support'}
          onPress={() => setActiveItem('support')}
        />
      </View>
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    backgroundColor: 'white',
    height: '100%',
    borderRightWidth: 1,
    borderRightColor: '#E5E7EB',
    position: 'relative',
  },
  toggleButton: {
    position: 'absolute',
    right: -12,
    top: 24,
    width: 24,
    height: 24,
    backgroundColor: 'white',
    borderRadius: 12,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: '#E5E7EB',
    zIndex: 1,
  },
  profileSection: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
    alignItems: 'center',
  },
  avatar: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: '#7C3AED',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 8,
  },
  avatarText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  userName: {
    fontWeight: 'bold',
    color: '#1F2937',
    textAlign: 'center',
  },
  userRole: {
    fontSize: 14,
    color: '#6B7280',
    textAlign: 'center',
  },
  navSection: {
    padding: 8,
  },
  navItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 8,
    borderRadius: 8,
    marginBottom: 4,
  },
  navItemActive: {
    backgroundColor: '#F3F4F6',
  },
  navIcon: {
    width: 24,
    height: 24,
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 12,
  },
  navLabel: {
    color: '#1F2937',
    fontSize: 14,
  },
  navLabelActive: {
    color: '#7C3AED',
    fontWeight: 'bold',
  },
  secondaryNav: {
    marginTop: 'auto',
    padding: 8,
    borderTopWidth: 1,
    borderTopColor: '#E5E7EB',
  },
});
