import React, { useState } from 'react';
import { View, Text, TextInput, ScrollView, Pressable, StyleSheet, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';

interface Lesson {
  id: string;
  title: string;
  duration: string;
  completed: boolean;
  timestamp?: number;
}

interface Module {
  id: string;
  title: string;
  lessons: Lesson[];
  progress: number;
}

interface LessonListSidebarProps {
  modules: Module[];
  currentLessonId: string;
  lastWatchedLessonId: string;
  onLessonSelect: (lessonId: string) => void;
  onResumeLastWatched: () => void;
}

export const LessonListSidebar = ({
  modules,
  currentLessonId,
  lastWatchedLessonId,
  onLessonSelect,
  onResumeLastWatched,
}: LessonListSidebarProps) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [expandedModules, setExpandedModules] = useState<string[]>([]);

  const toggleModule = (moduleId: string) => {
    setExpandedModules(prev =>
      prev.includes(moduleId)
        ? prev.filter(id => id !== moduleId)
        : [...prev, moduleId]
    );
  };

  const filteredModules = modules.map(module => ({
    ...module,
    lessons: module.lessons.filter(lesson =>
      lesson.title.toLowerCase().includes(searchQuery.toLowerCase())
    ),
  })).filter(module => module.lessons.length > 0);

  const calculateOverallProgress = () => {
    const totalLessons = modules.reduce((sum, module) => sum + module.lessons.length, 0);
    const completedLessons = modules.reduce((sum, module) => 
      sum + module.lessons.filter(lesson => lesson.completed).length, 0);
    return Math.round((completedLessons / totalLessons) * 100);
  };

  return (
    <View style={styles.container}>
      {/* Resume Button */}
      <Pressable style={styles.resumeButton} onPress={onResumeLastWatched}>
        <Feather name="play-circle" size={20} color="white" />
        <Text style={styles.resumeButtonText}>Resume Last Watched</Text>
      </Pressable>

      {/* Overall Progress */}
      <View style={styles.progressContainer}>
        <Text style={styles.progressText}>Course Progress: {calculateOverallProgress()}%</Text>
        <View style={styles.progressBar}>
          <View 
            style={[styles.progressFill, { width: `${calculateOverallProgress()}%` }]} 
          />
        </View>
      </View>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <Feather name="search" size={20} color="#6B7280" />
        <TextInput
          style={styles.searchInput}
          placeholder="Search lessons..."
          value={searchQuery}
          onChangeText={setSearchQuery}
        />
      </View>

      {/* Modules List */}
      <ScrollView style={styles.modulesList} showsVerticalScrollIndicator={false}>
        {filteredModules.map(module => (
          <View key={module.id} style={styles.moduleContainer}>
            <Pressable
              style={styles.moduleHeader}
              onPress={() => toggleModule(module.id)}
            >
              <View style={styles.moduleHeaderLeft}>
                <Feather
                  name={expandedModules.includes(module.id) ? 'chevron-down' : 'chevron-right'}
                  size={20}
                  color="#6B7280"
                />
                <Text style={styles.moduleTitle}>{module.title}</Text>
              </View>
              <Text style={styles.moduleProgress}>{module.progress}%</Text>
            </Pressable>

            {expandedModules.includes(module.id) && (
              <View style={styles.lessonsList}>
                {module.lessons.map(lesson => (
                  <Pressable
                    key={lesson.id}
                    style={[
                      styles.lessonItem,
                      currentLessonId === lesson.id && styles.currentLesson,
                    ]}
                    onPress={() => onLessonSelect(lesson.id)}
                  >
                    <View style={styles.lessonInfo}>
                      <View style={styles.lessonTitleRow}>
                        {lesson.completed ? (
                          <Feather name="check-circle" size={16} color="#059669" />
                        ) : currentLessonId === lesson.id ? (
                          <Feather name="play-circle" size={16} color="#7C3AED" />
                        ) : (
                          <View style={styles.lessonDot} />
                        )}
                        <Text 
                          style={[
                            styles.lessonTitle,
                            currentLessonId === lesson.id && styles.currentLessonText,
                          ]}
                        >
                          {lesson.title}
                        </Text>
                      </View>
                      <Text style={styles.lessonDuration}>{lesson.duration}</Text>
                    </View>
                  </Pressable>
                ))}
              </View>
            )}
          </View>
        ))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: Platform.OS === 'web' ? 320 : '100%',
    backgroundColor: 'white',
    borderRightWidth: Platform.OS === 'web' ? 1 : 0,
    borderRightColor: '#E5E7EB',
  },
  resumeButton: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#7C3AED',
    padding: 12,
    borderRadius: 8,
    margin: 16,
    gap: 8,
  },
  resumeButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: '600',
  },
  progressContainer: {
    padding: 16,
    paddingTop: 0,
  },
  progressText: {
    fontSize: 14,
    color: '#4B5563',
    marginBottom: 8,
  },
  progressBar: {
    height: 4,
    backgroundColor: '#E5E7EB',
    borderRadius: 2,
    overflow: 'hidden',
  },
  progressFill: {
    height: '100%',
    backgroundColor: '#7C3AED',
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 12,
    backgroundColor: '#F9FAFB',
    margin: 16,
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#E5E7EB',
  },
  searchInput: {
    flex: 1,
    marginLeft: 8,
    fontSize: 14,
    color: '#1F2937',
  },
  modulesList: {
    flex: 1,
  },
  moduleContainer: {
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
  },
  moduleHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 16,
  },
  moduleHeaderLeft: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
  },
  moduleTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#1F2937',
  },
  moduleProgress: {
    fontSize: 14,
    color: '#6B7280',
  },
  lessonsList: {
    paddingLeft: 16,
  },
  lessonItem: {
    padding: 12,
    paddingLeft: 16,
  },
  currentLesson: {
    backgroundColor: '#F5F3FF',
    borderLeftWidth: 3,
    borderLeftColor: '#7C3AED',
  },
  lessonInfo: {
    gap: 4,
  },
  lessonTitleRow: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
  },
  lessonDot: {
    width: 6,
    height: 6,
    borderRadius: 3,
    backgroundColor: '#D1D5DB',
  },
  lessonTitle: {
    fontSize: 14,
    color: '#4B5563',
    flex: 1,
  },
  currentLessonText: {
    color: '#1F2937',
    fontWeight: '500',
  },
  lessonDuration: {
    fontSize: 12,
    color: '#6B7280',
    marginLeft: 24,
  },
});
