import React, { useState } from 'react';
import { View, Text, TextInput, ScrollView, Pressable, StyleSheet, Platform } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { useTheme } from '../../context/ThemeContext';

interface Lesson {
  id: string;
  title: string;
  duration: string;
  completed: boolean;
  timestamp?: number;
}

interface Module {
  id: string;
  title: string;
  lessons: Lesson[];
  progress: number;
}

interface LessonListSidebarProps {
  modules?: Module[];
  currentLessonId?: string;
  lastWatchedLessonId?: string;
  onLessonSelect?: (lessonId: string) => void;
  onResumeLastWatched?: () => void;
}

export const LessonListSidebar = ({
  modules = [],
  currentLessonId = '',
  lastWatchedLessonId = '',
  onLessonSelect = () => {},
  onResumeLastWatched = () => {},
}: LessonListSidebarProps) => {
  const { colors } = useTheme();
  const [searchQuery, setSearchQuery] = useState('');
  const [expandedModules, setExpandedModules] = useState<string[]>([]);

  const toggleModule = (moduleId: string) => {
    setExpandedModules(prev =>
      prev.includes(moduleId)
        ? prev.filter(id => id !== moduleId)
        : [...prev, moduleId]
    );
  };

  const filteredModules = modules.map(module => ({
    ...module,
    lessons: module.lessons.filter(lesson =>
      lesson.title.toLowerCase().includes(searchQuery.toLowerCase())
    ),
  })).filter(module => module.lessons.length > 0);

  const calculateOverallProgress = () => {
    if (!modules.length) return 0;
    const totalLessons = modules.reduce((sum, module) => sum + module.lessons.length, 0);
    const completedLessons = modules.reduce((sum, module) => 
      sum + module.lessons.filter(lesson => lesson.completed).length, 0);
    return Math.round((completedLessons / totalLessons) * 100);
  };

  return (
    <View style={[styles.container, { backgroundColor: colors.surface }]}>
      {/* Resume Button */}
      <Pressable 
        style={[styles.resumeButton, { backgroundColor: colors.primary }]} 
        onPress={onResumeLastWatched}
      >
        <Feather name="play-circle" size={20} color={colors.onPrimary} />
        <Text style={[styles.resumeButtonText, { color: colors.onPrimary }]}>
          Resume Last Watched
        </Text>
      </Pressable>

      {/* Overall Progress */}
      <View style={styles.progressContainer}>
        <Text style={[styles.progressText, { color: colors.text }]}>
          Course Progress: {calculateOverallProgress()}%
        </Text>
        <View style={[styles.progressBar, { backgroundColor: colors.border }]}>
          <View 
            style={[
              styles.progressFill, 
              { 
                width: `${calculateOverallProgress()}%`,
                backgroundColor: colors.primary 
              }
            ]} 
          />
        </View>
      </View>

      {/* Search Bar */}
      <View style={[styles.searchContainer, { backgroundColor: colors.background }]}>
        <Feather name="search" size={20} color={colors.textSecondary} />
        <TextInput
          style={[styles.searchInput, { color: colors.text }]}
          placeholder="Search lessons..."
          placeholderTextColor={colors.textSecondary}
          value={searchQuery}
          onChangeText={setSearchQuery}
        />
      </View>

      {/* Modules List */}
      <ScrollView style={styles.modulesList} showsVerticalScrollIndicator={false}>
        {filteredModules.map(module => (
          <View key={module.id} style={styles.moduleContainer}>
            <Pressable
              style={[
                styles.moduleHeader,
                { backgroundColor: colors.background }
              ]}
              onPress={() => toggleModule(module.id)}
            >
              <View style={styles.moduleHeaderLeft}>
                <Feather
                  name={expandedModules.includes(module.id) ? 'chevron-down' : 'chevron-right'}
                  size={20}
                  color={colors.textSecondary}
                />
                <Text style={[styles.moduleTitle, { color: colors.text }]}>{module.title}</Text>
              </View>
              <View style={[styles.moduleProgress, { backgroundColor: colors.border }]}>
                <View 
                  style={[
                    styles.moduleProgressFill,
                    { 
                      width: `${module.progress}%`,
                      backgroundColor: colors.primary 
                    }
                  ]} 
                />
              </View>
            </Pressable>

            {expandedModules.includes(module.id) && (
              <View style={styles.lessonsList}>
                {module.lessons.map(lesson => (
                  <Pressable
                    key={lesson.id}
                    style={[
                      styles.lessonItem,
                      currentLessonId === lesson.id && { 
                        backgroundColor: colors.primary + '20'
                      }
                    ]}
                    onPress={() => onLessonSelect(lesson.id)}
                  >
                    <View style={styles.lessonInfo}>
                      {lesson.completed && (
                        <Feather name="check-circle" size={16} color={colors.primary} />
                      )}
                      <Text 
                        style={[
                          styles.lessonTitle,
                          { color: colors.text },
                          lesson.completed && { color: colors.textSecondary }
                        ]}
                      >
                        {lesson.title}
                      </Text>
                    </View>
                    <Text style={[styles.lessonDuration, { color: colors.textSecondary }]}>
                      {lesson.duration}
                    </Text>
                  </Pressable>
                ))}
              </View>
            )}
          </View>
        ))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: Platform.OS === 'web' ? 320 : '100%',
    borderRightWidth: 1,
    borderRightColor: '#E5E7EB',
  },
  resumeButton: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 12,
    margin: 16,
    borderRadius: 8,
    gap: 8,
  },
  resumeButtonText: {
    fontSize: 16,
    fontWeight: '600',
  },
  progressContainer: {
    padding: 16,
  },
  progressText: {
    fontSize: 14,
    marginBottom: 8,
  },
  progressBar: {
    height: 8,
    borderRadius: 4,
    overflow: 'hidden',
  },
  progressFill: {
    height: '100%',
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    margin: 16,
    padding: 8,
    borderRadius: 8,
    gap: 8,
  },
  searchInput: {
    flex: 1,
    fontSize: 14,
    padding: 0,
  },
  modulesList: {
    flex: 1,
  },
  moduleContainer: {
    marginBottom: 4,
  },
  moduleHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 16,
  },
  moduleHeaderLeft: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
    flex: 1,
  },
  moduleTitle: {
    fontSize: 16,
    fontWeight: '600',
  },
  moduleProgress: {
    width: 60,
    height: 4,
    borderRadius: 2,
    overflow: 'hidden',
  },
  moduleProgressFill: {
    height: '100%',
  },
  lessonsList: {
    paddingLeft: 16,
  },
  lessonItem: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 12,
    paddingLeft: 24,
  },
  lessonInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 8,
    flex: 1,
  },
  lessonTitle: {
    fontSize: 14,
  },
  lessonDuration: {
    fontSize: 12,
  },
});
