import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  useWindowDimensions,
  Platform,
  ScrollView,
  Pressable,
} from 'react-native';
import { VideoPlayer } from '../components/video/VideoPlayer';
import { LessonListSidebar } from '../components/course/LessonListSidebar';
import { CourseResources } from '../components/course/CourseResources';
import { LessonNotes } from '../components/course/LessonNotes';
import { CourseTabs } from '../components/course/CourseTabs';
import { Feather } from '@expo/vector-icons';

// Mock data - Replace with API calls
const mockCourse = {
  id: '1',
  title: 'Cybersecurity Fundamentals',
  modules: [
    {
      id: 'm1',
      title: 'Introduction to Cybersecurity',
      progress: 75,
      lessons: [
        {
          id: 'l1',
          title: 'What is Cybersecurity?',
          duration: '15:00',
          completed: true,
          videoId: '8ghzKMa9yNK',
          accessToken: '4e85f40a-dd2e-4a98-9de7-f2c986e77133',
          description: 'Learn about the basics of cybersecurity in this comprehensive lesson.',
          isLocked: false, // First lesson is free
        },
        {
          id: 'l2',
          title: 'Common Security Threats',
          duration: '20:00',
          completed: false,
          videoId: '8ghzKMa9yNK',
          accessToken: '4e85f40a-dd2e-4a98-9de7-f2c986e77133',
          description: 'Understand the common security threats and how to mitigate them.',
          isLocked: true,
        },
      ],
    },
    {
      id: 'm2',
      title: 'Network Security',
      progress: 25,
      lessons: [
        {
          id: 'l3',
          title: 'Understanding Network Protocols',
          duration: '25:00',
          completed: false,
          videoId: '8ghzKMa9yNK',
          accessToken: '4e85f40a-dd2e-4a98-9de7-f2c986e77133',
          description: 'Learn about network protocols and how they work in this detailed lesson.',
          isLocked: true,
        },
      ],
    },
  ],
  resourceCategories: [
    {
      id: 'cat1',
      title: 'Course Materials',
      resources: [
        {
          id: 'r1',
          title: 'Cybersecurity Basics PDF',
          type: 'pdf',
          url: 'https://example.com/cyber-basics.pdf',
          size: '2.5 MB',
          description: 'Comprehensive guide to cybersecurity fundamentals',
        },
        {
          id: 'r2',
          title: 'Network Diagram',
          type: 'image',
          url: 'https://example.com/network.png',
          size: '500 KB',
          preview: 'https://picsum.photos/200/300',
          description: 'Visual representation of network architecture',
        },
      ],
    },
    {
      id: 'cat2',
      title: 'Additional Resources',
      resources: [
        {
          id: 'r3',
          title: 'Security Tools Setup Guide',
          type: 'pdf',
          url: 'https://example.com/setup-guide.pdf',
          size: '1.8 MB',
          description: 'Step-by-step guide for setting up security tools',
        },
        {
          id: 'r4',
          title: 'Practice Code Examples',
          type: 'code',
          url: 'https://example.com/code-examples.zip',
          size: '1.2 MB',
          description: 'Sample code for security implementations',
        },
      ],
    },
  ],
};

type Tab = 'lessons' | 'resources' | 'notes' | 'qa';

export const CoursePlayerScreen = () => {
  const [currentLesson, setCurrentLesson] = useState(mockCourse.modules[0].lessons[0]);
  const [isSubscribeModalVisible, setIsSubscribeModalVisible] = useState(false);
  const { width } = useWindowDimensions();
  const [showSidebar, setShowSidebar] = useState(width >= 1024);
  const [currentTime, setCurrentTime] = useState(0);
  const [activeTab, setActiveTab] = useState<'notes' | 'resources'>('notes');

  useEffect(() => {
    const handleResize = () => {
      setShowSidebar(window.innerWidth >= 1024);
    };

    if (Platform.OS === 'web') {
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }
  }, []);

  const handleProgress = (progress: number) => {
    // Update progress in your backend
    console.log('Progress:', progress);
  };

  const handleLessonComplete = () => {
    // Mark lesson as completed in your backend
    console.log('Lesson completed');
  };

  const handleSubscribeClick = () => {
    setIsSubscribeModalVisible(true);
  };

  const allLessons = mockCourse.modules.flatMap(module => module.lessons);

  return (
    <View style={styles.container}>
      <View style={styles.content}>
        <View style={styles.mainContent}>
          <View style={styles.videoContainer}>
            <VideoPlayer
              videoId={currentLesson.videoId}
              accessToken={currentLesson.accessToken}
              onComplete={handleLessonComplete}
              onProgress={handleProgress}
              onTimeUpdate={setCurrentTime}
              initialProgress={currentLesson.progress || 0}
              isLocked={currentLesson.isLocked}
              onSubscribeClick={handleSubscribeClick}
            />
          </View>

          <View style={styles.lessonInfo}>
            <Text style={styles.lessonTitle}>{currentLesson.title}</Text>
            <Text style={styles.lessonDescription}>{currentLesson.description}</Text>
          </View>

          <CourseTabs activeTab={activeTab} onTabChange={setActiveTab} />

          {activeTab === 'notes' ? (
            <LessonNotes lessonId={currentLesson.id} currentTime={currentTime} />
          ) : (
            <CourseResources categories={mockCourse.resourceCategories} />
          )}
        </View>

        {(showSidebar || Platform.OS !== 'web') && (
          <LessonListSidebar
            lessons={allLessons}
            currentLessonId={currentLesson.id}
            onLessonSelect={setCurrentLesson}
            onClose={() => setShowSidebar(false)}
            onSubscribeClick={handleSubscribeClick}
          />
        )}
      </View>

      {isSubscribeModalVisible && (
        <View style={styles.modalOverlay}>
          <View style={styles.modal}>
            <Text style={styles.modalTitle}>Subscribe to Access All Content</Text>
            <Text style={styles.modalText}>
              Get unlimited access to all courses and features with our premium subscription.
            </Text>
            <View style={styles.modalButtons}>
              <Pressable
                style={[styles.modalButton, styles.modalButtonPrimary]}
                onPress={() => {
                  // Handle subscription purchase
                  setIsSubscribeModalVisible(false);
                }}
              >
                <Text style={styles.modalButtonText}>Subscribe Now</Text>
              </Pressable>
              <Pressable
                style={styles.modalButton}
                onPress={() => setIsSubscribeModalVisible(false)}
              >
                <Text style={[styles.modalButtonText, styles.modalButtonTextSecondary]}>
                  Maybe Later
                </Text>
              </Pressable>
            </View>
          </View>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9FAFB',
  },
  content: {
    flex: 1,
    flexDirection: 'row',
  },
  mainContent: {
    flex: 1,
    padding: 24,
  },
  videoContainer: {
    aspectRatio: 16 / 9,
    backgroundColor: '#000',
    borderRadius: 12,
    overflow: 'hidden',
    marginBottom: 24,
  },
  lessonInfo: {
    marginBottom: 24,
  },
  lessonTitle: {
    fontSize: 24,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 8,
  },
  lessonDescription: {
    fontSize: 14,
    color: '#6B7280',
  },
  modalOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: 'white',
    borderRadius: 12,
    padding: 24,
    width: '90%',
    maxWidth: 400,
  },
  modalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#1F2937',
    marginBottom: 12,
  },
  modalText: {
    fontSize: 16,
    color: '#6B7280',
    marginBottom: 24,
  },
  modalButtons: {
    flexDirection: 'row',
    justifyContent: 'flex-end',
    gap: 12,
  },
  modalButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 6,
  },
  modalButtonPrimary: {
    backgroundColor: '#7C3AED',
  },
  modalButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: 'white',
  },
  modalButtonTextSecondary: {
    color: '#6B7280',
  },
});
