import React from 'react';
import { View, Text, Pressable, StyleSheet } from 'react-native';
import { Feather } from '@expo/vector-icons';

interface SidebarProps {
  currentRoute: string;
  navigation: {
    navigate: (route: string) => void;
  };
}

const menuItems = [
  {
    id: 'home',
    label: 'Home',
    icon: 'home',
  },
];

export const Sidebar = ({ currentRoute, navigation }: SidebarProps) => {
  return (
    <View style={styles.sidebar}>
      <View style={styles.menu}>
        {menuItems.map((item) => (
          <Pressable
            key={item.id}
            style={[
              styles.menuItem,
              currentRoute === item.id && styles.activeMenuItem,
            ]}
            onPress={() => navigation.navigate(item.id)}
          >
            <Feather
              name={item.icon as any}
              size={20}
              color={currentRoute === item.id ? '#7C3AED' : '#6B7280'}
            />
            <Text
              style={[
                styles.menuText,
                currentRoute === item.id && styles.activeMenuText,
              ]}
            >
              {item.label}
            </Text>
          </Pressable>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    width: 280,
    backgroundColor: 'white',
    borderRightWidth: 1,
    borderRightColor: '#E5E7EB',
  },
  menu: {
    padding: 16,
  },
  menuItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 12,
    borderRadius: 8,
    marginBottom: 4,
  },
  activeMenuItem: {
    backgroundColor: '#F5F3FF',
  },
  menuText: {
    marginLeft: 12,
    fontSize: 16,
    color: '#6B7280',
  },
  activeMenuText: {
    color: '#7C3AED',
    fontWeight: '500',
  },
});
