import React from 'react';
import { View, Text, ScrollView, StyleSheet, Platform } from 'react-native';
import { CourseCard } from '../components/course/CourseCard';
import { useTheme } from '../context/ThemeContext';

// Mock data for testing
const mockCourses = [
  {
    id: '1',
    title: 'Complete Web Development',
    category: 'Web Development',
    duration: '20h 30m',
    rating: 4.8,
    thumbnail: 'https://picsum.photos/800/600',
    instructor: 'John Doe'
  },
  {
    id: '2',
    title: 'Mobile App Development',
    category: 'Mobile Development',
    duration: '18h 45m',
    rating: 4.9,
    thumbnail: 'https://picsum.photos/800/600?random=2',
    instructor: 'Jane Smith'
  },
  {
    id: '3',
    title: 'Data Science Fundamentals',
    category: 'Data Science',
    duration: '15h 15m',
    rating: 4.7,
    thumbnail: 'https://picsum.photos/800/600?random=3',
    instructor: 'Mike Johnson'
  }
];

export const HomeScreen = ({ navigation }) => {
  const { colors } = useTheme();

  return (
    <ScrollView 
      style={[styles.container, { backgroundColor: colors.background }]}
      contentContainerStyle={styles.contentContainer}
      showsVerticalScrollIndicator={false}
    >
      <View style={[styles.header, { backgroundColor: colors.surface }]}>
        <Text style={[styles.welcomeText, { color: colors.text }]}>Welcome to SKiddy</Text>
        <Text style={[styles.subText, { color: colors.textSecondary }]}>Start your learning journey today</Text>
      </View>

      <View style={styles.coursesSection}>
        <Text style={[styles.sectionTitle, { color: colors.text }]}>Featured Courses</Text>
        <View style={styles.courseGrid}>
          {mockCourses.map((course) => (
            <View key={course.id} style={styles.cardWrapper}>
              <CourseCard
                title={course.title}
                category={course.category}
                duration={course.duration}
                rating={course.rating}
                thumbnail={course.thumbnail}
                instructor={course.instructor}
                onPress={() => navigation.navigate('CoursePlayer', { courseId: course.id })}
              />
            </View>
          ))}
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  contentContainer: {
    padding: 24,
  },
  header: {
    marginBottom: 32,
    borderRadius: 12,
    padding: 24,
    ...Platform.select({
      web: {
        boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06)',
      },
      default: {
        elevation: 2,
      },
    }),
  },
  welcomeText: {
    fontSize: 32,
    fontWeight: '700',
    marginBottom: 8,
  },
  subText: {
    fontSize: 18,
  },
  coursesSection: {
    marginBottom: 32,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: '600',
    marginBottom: 24,
  },
  courseGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: 24,
    justifyContent: Platform.OS === 'web' ? 'flex-start' : 'center',
  },
  cardWrapper: {
    flex: Platform.OS === 'web' ? 1 : undefined,
    minWidth: Platform.OS === 'web' ? 300 : undefined,
    maxWidth: Platform.OS === 'web' ? 400 : 340,
  }
});
