import React from 'react';
import { View, Text, StyleSheet, ScrollView, Pressable, Linking } from 'react-native';
import { Feather } from '@expo/vector-icons';

interface Resource {
  id: string;
  title: string;
  type: 'pdf' | 'code' | 'link' | 'video';
  url: string;
  size?: string;
}

interface CourseResourcesProps {
  resources: Resource[];
}

export const CourseResources = ({ resources }: CourseResourcesProps) => {
  const getIconName = (type: Resource['type']) => {
    switch (type) {
      case 'pdf':
        return 'file-text';
      case 'code':
        return 'code';
      case 'link':
        return 'external-link';
      case 'video':
        return 'video';
      default:
        return 'file';
    }
  };

  const handleResourceClick = async (resource: Resource) => {
    try {
      await Linking.openURL(resource.url);
    } catch (error) {
      console.error('Error opening resource:', error);
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Course Resources</Text>
        <Text style={styles.subtitle}>
          Download supplementary materials and access external resources
        </Text>
      </View>

      <View style={styles.resourceList}>
        {resources.map((resource) => (
          <Pressable
            key={resource.id}
            style={styles.resourceItem}
            onPress={() => handleResourceClick(resource)}
          >
            <View style={styles.resourceIcon}>
              <Feather name={getIconName(resource.type)} size={24} color="#6B7280" />
            </View>
            <View style={styles.resourceInfo}>
              <Text style={styles.resourceTitle}>{resource.title}</Text>
              {resource.size && (
                <Text style={styles.resourceSize}>{resource.size}</Text>
              )}
            </View>
            <Feather name="download" size={20} color="#6B7280" />
          </Pressable>
        ))}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
  },
  header: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#E5E7EB',
  },
  title: {
    fontSize: 20,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 4,
  },
  subtitle: {
    fontSize: 14,
    color: '#6B7280',
  },
  resourceList: {
    padding: 16,
  },
  resourceItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 12,
    backgroundColor: '#F9FAFB',
    borderRadius: 8,
    marginBottom: 8,
  },
  resourceIcon: {
    width: 40,
    height: 40,
    borderRadius: 8,
    backgroundColor: '#F3F4F6',
    alignItems: 'center',
    justifyContent: 'center',
  },
  resourceInfo: {
    flex: 1,
    marginLeft: 12,
    marginRight: 12,
  },
  resourceTitle: {
    fontSize: 16,
    color: '#374151',
    marginBottom: 2,
  },
  resourceSize: {
    fontSize: 14,
    color: '#6B7280',
  },
});
